import { ImageBackground, StyleSheet, Text, View } from 'react-native';

const page1text = ({height, width}) =>{
    return(
        <View style={{width: width, height: height, backgroundColor:'red', justifyContent:'center', alignItems:'center'}}>
            <Text>All You can Eat</Text>
            <View style={{width:'80%', height:2, backgroundColor:'#9C8B8B', marginBottom:2}}></View>
            <View style={{width:'80%', height:3, backgroundColor:'white'}}></View>
            <Text>A pranzo</Text>
            <Text>da Lunedì al venerdì</Text>
            <View style={{width:120, height:100, backgroundColor:'yellow', position:'relative'}}>
                <Text style={{fontSize:48, position:'absolute', bottom:20}}>,90</Text>
                <Text style={{fontSize:48, }}>24</Text>

            </View>

        </View>
    );
}

export default page1text;
