import { ImageBackground, StyleSheet, Text, View } from 'react-native';

const page1text = ({height, width}) =>{
    return(
        <View style={{width: width, height: height, backgroundColor:'red', justifyContent:'center', alignItems:'center'}}>
            <Text style={{color:'white', marginBottom:15}}>All You can Eat</Text>
            <View style={{width:'80%', height:2, backgroundColor:'#9C8B8B', marginBottom:2,color:'white'}}></View>
            <View style={{width:'80%', height:3, backgroundColor:'white',color:'white', marginBottom:15}}></View>
            <Text style={{color:'white', marginBottom:5}}>A pranzo</Text>
            <Text style={{color:'white', marginBottom:15}}>da Lunedì al venerdì</Text>
            <View style={{width:150, height:80,position:'relative', margin}}>
                <Text style={{fontSize:36, position:'absolute', right:40, color:'white'}}>,90</Text>
                <Text style={{fontSize:48, position:'absolute', top:20, marginRight:5, color:'white'}}>24</Text>
                <Text style={{fontSize:70, color:'white', position:'absolute', right:0}}>€</Text>
            </View>
            <Text style={{color:'white'}}>BEVANDE E DOLCI ESCLUSE</Text>
            <Text style={{color:'white'}}>I bambini &lt; 140cm pagano 0,00 €</Text>


        </View>
    );
}

export default page1text;
