import { ImageBackground, StyleSheet, Text, View , Image, TouchableWithoutFeedback} from 'react-native';

const slider = ({width, height, state}) =>{

    const animatedStart = useRef(new Animated.Value(0)).current;
    const animatedEnd = useRef(new Animated.Value(state)).current;

    useEffect(() => {
      Animated.timing(animatedStart, {
          toValue: animatedEnd,
          duration: 300,
          useNativeDriver:false,
      }).start();
    }, []);

    return(
   <View style={{width:width, height:height, borderRadius:45, marginTop:40}}>
       <View style={{backgroundColor:'white', width:'100%', height:'100%', opacity:0.3, borderRadius:45, position:'relative'}}></View>
       <View style={{width:animatedStart, height:'100%', backgroundColor:'white', position:'absolute', borderRadius:45}}></View>
   </View>
    );
}

export default slider;
